import React, { Component } from 'react'

import Newsitems from './Newsitems'

export class News extends Component {
    articles= []
    constructor(){
        super();
        console.log("helllon i am a constructor");
        this.state={
             articles: [],
             loading:false,
             page:1
        }
    }
async componentDidMount(){
    let url="https://newsapi.org/v2/everything?q=tesla&from=2023-01-09&sortBy=publishedAt&apiKey=9e4b83360f054ff884a26e5dae505e72"
    let data= await fetch(url);
    let parsedData= await data.json()
    console.log(parsedData);
    this.setState({articles: parsedData.articles})

}
handlePrevclick=()=>{
    console.log("previos")

}
handleNextclick=()=>{
    console.log("next")
}

  render() {
    return (
      <div className="container my-3">
        <h1> Daily News - Top Headlines</h1>
        <div className="row">
        {this.state.articles.map((element)=>{

      return <div className="col-md-3" key ={element.url}>
        
       <Newsitems title={element.title}description={element.description} imgurl={element.urlToImage} newsUrl={element.url}/>
       </div>
        })}

      
      
       
       </div>
      <div className="container d-flex justify-content-between">
        <button disabled={this.state.page<=1}type="button" class="btn btn-dark" onClick={this.handlePrevclick}>&larr;Previous</button>
      <button type="button" class="btn btn-dark" onClick={this.handleNextclick}> Next &rarr;</button>
      </div>
      </div>
    )
  }
}

export default News
